        --threshold 0.1 \
        --rho 1 \
        --eval_batch_size 1 \
        --rng_seed 0 \
    --max_seconds 3000 \
    --optimization_steps_per_output 5 \
    --proposals_per_round 1 \
    --output_path ./docker_entrypoint_test/adalead_gata2 \
    --seed_sequence  \
